import { Component, Input } from '@angular/core';

@Component({
  selector: 'app-summary',
  templateUrl: `
        <div> <label>Sims Count</label> <span>{{ simsCount }}</span> </div>
  `
})
export class SummaryComponent {

  @Input() simsCount: number;
  
}
